import React, { Component } from "react";
import "./Die.css";

class Die extends Component {
  state = {
    class: "fas fa-dice-",
    dice: ["one", "two", "three", "four", "five", "six"]
  };

  render() {
    const diceClass = this.state.class + this.state.dice[this.props.val];
    console.log(diceClass);

    return (
      <button
        className={"Die"}
        style={{ backgroundColor: this.props.locked ? "grey" : "black" }}
        onClick={this.props.rollsLeft > 0 ? this.props.handleClick : null}
      >
        <i className={this.state.class}></i>
      </button>
    );
  }
}

export default Die;
