import React, { Component } from "react";
import "./Die.css";

class Die extends Component {
  render() {
    return (
      <button
        className={"Die"}
        style={{
          backgroundColor:
            this.props.locked && this.props.rollsLeft === 0 ? "grey" : "black"
        }}
        onClick={this.props.handleClick}
      >
        {this.props.val}
      </button>
    );
  }
}

export default Die;
