import React, { Component } from "react";
import Die from "./Die";
import "./Dice.css";

class Dice extends Component {
  render() {
    return (
      <div className="Dice">
        {this.props.dice.map((d, idx) => (
          <Die
            handleClick={() => this.props.handleClick(idx)}
            val={d}
            locked={this.props.locked[idx]}
            idx={idx}
            key={idx}
            rollsLeft={this.props.rollsLeft}
            rolling={this.props.rolling[idx]}
          />
        ))}
      </div>
    );
  }
}

export default Dice;
