import React, { Component } from "react";
import RuleRow from "./RuleRow";
import "./ScoreTable.css";
import {
  ones,
  twos,
  threes,
  fours,
  fives,
  sixes,
  threeOfKind,
  fourOfKind,
  fullHouse,
  smallStraight,
  largeStraight,
  yahtzee,
  chance
} from "./Rules";

class ScoreTable extends Component {
  render() {
    const { scores, doScore } = this.props;

    return (
      <div className="ScoreTable">
        <section className="ScoreTable-section">
          <h2>Upper</h2>
          <table cellSpacing="0">
            <tbody>
              <RuleRow
                description="1 point per 1"
                name="Ones"
                score={scores.ones}
                doScore={evt => doScore("ones", ones.evalRoll)}
              />
              <RuleRow
                description="2 point per 2"
                name="Twos"
                score={scores.twos}
                doScore={evt => doScore("twos", twos.evalRoll)}
              />
              <RuleRow
                description="3 point per 3"
                name="Threes"
                score={scores.threes}
                doScore={evt => doScore("threes", threes.evalRoll)}
              />
              <RuleRow
                description="4 point per 4"
                name="Fours"
                score={scores.fours}
                doScore={evt => doScore("fours", fours.evalRoll)}
              />
              <RuleRow
                description="5 point per 5"
                name="Fives"
                score={scores.fives}
                doScore={evt => doScore("fives", fives.evalRoll)}
              />
              <RuleRow
                description="6 point per 6"
                name="Sixes"
                score={scores.sixes}
                doScore={evt => doScore("sixes", sixes.evalRoll)}
              />
            </tbody>
          </table>
        </section>
        <section className="ScoreTable-section ScoreTable-section-lower">
          <h2>Lower</h2>
          <table cellSpacing="0">
            <tbody>
              <RuleRow
                description="Sum all dice if 3 are the same"
                name="Three of Kind"
                score={scores.threeOfKind}
                doScore={evt => doScore("threeOfKind", threeOfKind.evalRoll)}
              />
              <RuleRow
                description="Sum all dice if 4 are the same"
                name="Four of Kind"
                score={scores.fourOfKind}
                doScore={evt => doScore("fourOfKind", fourOfKind.evalRoll)}
              />
              <RuleRow
                description="25 points for a full house"
                name="Full House"
                score={scores.fullHouse}
                doScore={evt => doScore("fullHouse", fullHouse.evalRoll)}
              />
              <RuleRow
                description="30 points for a small straight"
                name="Small Straight"
                score={scores.smallStraight}
                doScore={evt =>
                  doScore("smallStraight", smallStraight.evalRoll)
                }
              />
              <RuleRow
                description="40 points for a small straight"
                name="Large Straight"
                score={scores.largeStraight}
                doScore={evt =>
                  doScore("largeStraight", largeStraight.evalRoll)
                }
              />
              <RuleRow
                description="50 points for yahtzee"
                name="Yahtzee"
                score={scores.yahtzee}
                doScore={evt => doScore("yahtzee", yahtzee.evalRoll)}
              />
              <RuleRow
                description="Summ of all dice"
                name="Chance"
                score={scores.chance}
                doScore={evt => doScore("chance", chance.evalRoll)}
              />
            </tbody>
          </table>
        </section>
        <section className="ScoreTable-section">
          <h2>Lower</h2>
        </section>
      </div>
    );
  }
}

export default ScoreTable;
