import React, { Component } from "react";
import Die from "./Die";
import "./Dice.css";

class Dice extends Component {
  render() {
    return (
      <div className="Dice">
        {this.props.dice.map((d, idx) => (
          <Die
            handleClick={() => this.props.handleClick(idx)}
            val={d}
            locked={
              (this.props.rollsLeft = 0 ? "true" : this.props.locked[idx])
            }
            idx={idx}
            key={idx}
          />
        ))}
      </div>
    );
  }
}

export default Dice;
